@import '../styles/mixins.scss';
@import '../styles/variables.scss';

@media (min-width: $mobile-width) {
    
    .header {
        display: flex;
        flex-direction: column;
        width: 100%;
        padding: 15px 20px 0 20px;
        box-sizing: border-box;
    }

    .header__item {
        padding-bottom: 10px;
    }

    .nav {
        position: relative;
        width: 100%;
    }

    .nav__list {
        margin: 0;
        padding: 0;
        list-style: none;
    }

    .nav__item {
        border-bottom: solid 1px #000000;
        cursor: pointer;
    
        &:first-child {
            border-top: 1px solid #000000
        }
    }

    .nav__link {
        @include main-text;
        display: block;
        padding: 10px 0 10px 0;
        text-decoration: none;

        &:hover {
            color: #1F2041;
        }
    }
}

@media (max-width: $mobile-width-only) {
    
    .nav--closed::before {
        content: "";
        position: absolute;
        top: -48px;
        right: 32px;
        width: 20px;
        height: 2px;
        background-color: $main-color;
        box-shadow: 0 6px 0 0 $main-color, 0 12px 0 0 $main-color;
        z-index: 1000;
    }

    .nav--opened {
        &::before, &::after {
            content: "";
            position: absolute;
            width: 25px;
            height: 2px;
            background-color: $main-color;
        }

        &::before {
            top: -42px;
            right: 28px;
            transform: rotate(45deg);
        }

        &::after {
            top: -42px;
            right: 28px;
            transform: rotate(-45deg);
        }
    }

    .nav__toggle {
        position: absolute;
        top: -50px;
        right: 30px;
        width: 21px;
        height: 20px;
        font-size: 0;
        background-color: transparent;
        border: 0;
        outline: 0;
        cursor: pointer;
    }

    .nav--closed .nav__list{
        display: none;
      }
}

