@import '../styles/mixins.scss';

@media (min-width: 320px) {
    
    .header {
        display: flex;
        flex-direction: column;
        width: 100%;
    }

    .nav {
        position: relative;
        width: 100%;
    }

    .nav__list {
        list-style: none;
    }

    .nav__link {
        @include main-text;
        text-decoration: none;
    }
}

